/*...................................................................*/
/*      width: Return the width in bytes of the tile                 */
/*                                                                   */
/*     Return: The tile width in bytes                               */
/*...................................................................*/
#define width() (16 / 8)

/*...................................................................*/
/*     height: Return the height in bytes of the tile                */
/*                                                                   */
/*     Return: The tile height in bytes                              */
/*...................................................................*/
#define height() (16)

/*...................................................................*/
/* total_width: Total width of entire bit map                        */
/*                                                                   */
/*     Return: The tile height in bytes                              */
/*...................................................................*/
#define total_width() (16 * width())

// Global functions

/*...................................................................*/
/* TilePixel: Return whether or not tile pixle bit is set to one     */
/*                                                                   */
/*      Input: tileSetData the tile set data to reference into       */
/*             tileNum the index into the tile set data              */
/*             x the width positional bit to check                   */
/*             y the height positional bit to check                  */
/*                                                                   */
/*     Return: One (1) if pixel should be illuminated, 0 otherwise   */
/*...................................................................*/
int TilePixel(u8 *tileSetData, int tileNum, u32 x, u32 y)
{
  u32 tilebit;
  int tileY = (tileNum * width()) / total_width();
  int tileX = ((tileNum * width()) & 0x1F) / width();
  u8 *tileColumn = tileSetData +((tileY * height()) +y) *total_width();
  u8 *tileRow = tileColumn + (tileX * width());

  // Ensure x and y inputs are within the valid font size
  if (y >= height())
  {
    return 0;
  }
  if (x >= width() * 8)
  {
    return 0;
  }

  // Shift font bit left based on character width
  // Framebuffer and font map are little endian, so first pixel is low
  // (bit zero or ((1 << 7) >> CHARACTER_WIDTH) and as x increases to
  // higher bits until 7 ((1 << 7) << 0).
  if (x < 8)
  {
    tilebit = ((1 << 7) >> x);

    // Reference this bit in first byte of tile data. Return if tile
    // bit set or not.
    return ((tileRow[0] & tilebit) ? 1 : 0);
  }
  else
  {
    tilebit = ((1 << 7) >> (x - 8));

    // Reference this bit in second byte of tile data. Return if tile
    // bit set or not.
    return ((tileRow[1] & tilebit) ? 1 : 0);
  }
}

